CREATE TABLE comments(
CommentID int(11) NOT NULL AUTO_INCREMENT,
Comment varchar(1370) NOT NULL,
PRIMARY KEY(CommentID)
) ENGINE=InnoDB;


CREATE TABLE genres(
GenreID int(11) NOT NULL AUTO_INCREMENT,
GenreName varchar(255) NOT NULL,
PRIMARY KEY(GenreID)
) ENGINE=InnoDB;


CREATE TABLE links(
LinkID int(11) NOT NULL AUTO_INCREMENT,
Source varchar(255) NOT NULL,
PRIMARY KEY (LinkID)
) ENGINE=InnoDB;


CREATE TABLE pictures(
PicID int(11) NOT NULL AUTO_INCREMENT,
PicName varchar(255) NOT NULL,
PRIMARY KEY(PicID)
) ENGINE=InnoDB;


CREATE TABLE studios(
StudioID int(11) NOT NULL AUTO_INCREMENT,
StudioName varchar(255) NOT NULL,
PRIMARY KEY(StudioID)
) ENGINE=InnoDB;


CREATE TABLE users(
Login varchar(255) NOT NULL,
Password varchar(255) NOT NULL,
Level int(11) NOT NULL,
PRIMARY KEY(Login)
) ENGINE=InnoDB;


CREATE TABLE list(
ID int(11) NOT NULL AUTO_INCREMENT,
Title varchar(255) NOT NULL,
SeriesNumber int(11) NOT NULL,
Type varchar(255) NOT NULL,
GenreID int(11) NOT NULL,
StudioID int(11) NOT NULL,
PRIMARY KEY(ID),
FOREIGN KEY(GenreID) REFERENCES genres(GenreID),
FOREIGN KEY(StudioID) REFERENCES studios(StudioID)
) ENGINE=InnoDB;


DELIMITER $$
CREATE TRIGGER adm BEFORE DELETE ON users FOR EACH ROW IF OLD.Level='1' THEN INSERT INTO users VALUES ("", "", -1); END IF
$$ DELIMITER ;

DELIMITER $$
CREATE TRIGGER trig AFTER INSERT ON users FOR EACH ROW IF NEW.Level<0 OR NEW.Level>1 THEN DELETE FROM users WHERE users.Level<0 OR users.Level>1; END IF
$$ DELIMITER ;